settings.custom_liquid:
{% assign collection_handle = page.metafields.custom.collections %}
{% assign collection = collections[collection_handle] %}

{% if collection != blank %}
  <div class="product-grid">
    {% for product_card_product in collection.products %}
      {% assign product_swatch_option = settings.swatch | downcase %}
      {% assign isColor = false %}
      {% assign variant_img = '' %}
      {% for option in product_card_product.options_with_values %}
        {% assign option_name = option.name | downcase %}
        {% if product_swatch_option contains option_name %}
          {% assign isColor = true %}
          {% assign index = forloop.index0 %}
          {% assign position = forloop.index | minus: 1 %}
          {% assign variant_selected = option.selected_value %}
          {% if settings.show_quick_shop %}
            {% for variant in product_card_product.variants limit: 1 %}
              {% if variant.image != null %}
                {% assign variant_img = variant.image %}
              {% endif %}
            {% endfor %}
          {% endif %}
          {% break %}
        {% else %}
          {% assign position = 0 %}
          {% assign variant_selected = nil %}
        {% endif %}
      {% endfor %}

      <div class="product-item" data-product-id="{{ product_card_product.id }}" data-json-product='{"id": {{product_card_product.id}},"handle": "{{product_card_product.handle}}","media": {{product.media | json | escape}},"variants": {{ product_card_product.variants | json | escape }}}'>
        <div class="card">
          <div class="card-product{% if product_card_product.featured_media == nil %} card--text-only card--soft{% endif %}">
            <div class="card-product__wrapper">
              <div class="card-media">
                {% render 'image-product-card',
                  product_card_product: product_card_product,
                  variant_img: variant_img,
                  loading: loading
                %}
                <a class="card-link" href="{{ product_card_product.url }}" title="{{ product_card_product.title | escape }}"></a>
              </div>

              {% if settings.show_badge %}
                {% render 'product-badge',
                  product: product_card_product,
                  class: 'card__badge',
                  badge_detail: true,
                  serial: serial
                %}
              {% endif %}
            </div>
          </div>
          <div class="card-information">
            <div class="card-information__wrapper text-{{ settings.product_content_text_align }}">
              {% if product_card_product.metafields.custom.dots %}
                <div class="metafield-dots">
                  {% assign formatted_values = product_card_product.metafields.custom.dots | replace: '[', '' | replace: '"]', '' %}
                  {% assign formatted_values = formatted_values | remove_first: '"' %}
                  {% assign formatted_values_array = formatted_values | split: '","' %}
                  {{ formatted_values_array | join: ' • ' | lstrip }}
                </div>
              {% endif %}

              <a class="card-title link-underline card-title-ellipsis{% if variant_selected != blank %} card-title-change{% endif %}" href="{{ product_card_product.url }}" data-product-title="{{ product_card_product.title | escape }}" data-product-url="{{ product_card_product.url }}">
                <span class="text">
                  {{ product_card_product.title | escape }}
                  {% if variant_selected != blank %}
                    <span data-change-title> {{ variant_selected }}</span>
                  {% endif %}
                </span>
              </a>

              <div class="card-price">
                {% render 'price',
                  product: product_card_product,
                  price_class: '',
                  hasCountdown: hasCountdown
                %}
              </div>

              <div class="card-action">
                {% render 'add-to-cart-form',
                  product: product_card_product,
                  id: 'form',
                  position: position,
                  sectionId: sectionId
                %}
              </div>
            </div>
          </div>
        </div>
      </div>
    {% endfor %}
  </div>
{% else %}
  <p>No collection found.</p>
{% endif %}

<style> .product-grid { display: flex; flex-wrap: wrap; gap: 10px; padding: 10px; } .product-item { flex: 0 0 calc(50% - 10px); max-width: calc(50% - 10px); } @media screen and (min-width: 768px) { .product-item { flex: 0 0 calc(20% - 20px); max-width: calc(20% - 20px); } .product-grid { gap: 20px; padding: 20px; } } .product-item { position: relative; } .card { background-color: #fff; border-radius: 8px; box-shadow: 0 2px 6px rgba(0, 0, 0, 0.1); overflow: hidden; } .card-product__wrapper { position: relative; } .card-media { position: relative; padding-top: 150%; /* Adjusted padding-top for portrait orientation */ overflow: hidden; } .card-media img { position: absolute; top: 0; left: 0; width: 100%; height: 100%; object-fit: cover; border-radius: 8px 8px 0 0; } .card-link { position: absolute; top: 0; left: 0; width: 100%; height: 100%; z-index: 1; } .card__badge { position: absolute; top: 5px; left: 5px; font-size: 10px; padding: 3px 6px; } .card-information { padding: 8px; } .metafield-dots { font-size: 10px; color: #999; margin-bottom: 3px; white-space: nowrap; overflow: hidden; text-overflow: ellipsis; } .card-title { font-size: 12px; font-weight: bold; margin-bottom: 3px; white-space: nowrap; overflow: hidden; text-overflow: ellipsis; } .card-title-change span[data-change-title] { font-weight: normal; color: #999; } .card-price { font-size: 14px; font-weight: bold; color: #333; margin-bottom: 8px; } .card-action { margin-top: 8px; text-align: center; } .card-action .button { display: block; width: 100%; padding: 8px; background-color: #000; color: #fff; border: none; border-radius: 4px; font-size: 12px; font-weight: bold; text-transform: uppercase; cursor: pointer; } </style>
