{% assign chakra_list = product.metafields.custom.chakra_list.value %}

<div class="container">
  <div class="row">
    <div class="col-12">
      <h2 class="text-center mb-4">
        <i class="fas fa-om me-2 chakra-icon-animate"></i> Chakras Connected
      </h2>
    </div>
  </div>
  <div class="row">
    <div class="col-12">
      <div class="chakra-icons d-flex justify-content-center">
        {% for chakra in chakra_list %}
        <div class="chakra-icon-wrapper">
          {% case chakra %}
          {% when 'Root Chakra' %}
          <div class="chakra-icon">
            {% render 'root-chakra-icon' %}
            <div class="chakra-name">Root Chakra</div>
          </div>
          {% when 'Sacral Chakra' %}
          <div class="chakra-icon">
            {% render 'sacral-chakra-icon' %}
            <div class="chakra-name">Sacral Chakra</div>
          </div>
          {% when 'Solar Plexus Chakra' %}
          <div class="chakra-icon">
            {% render 'solar-plexus-chakra-icon' %}
            <div class="chakra-name">Solar Plexus Chakra</div>
          </div>
          {% when 'Heart Chakra' %}
          <div class="chakra-icon">
            {% render 'heart-chakra-icon' %}
            <div class="chakra-name">Heart Chakra</div>
          </div>
          {% when 'Throat Chakra' %}
          <div class="chakra-icon">
            {% render 'throat-chakra-icon' %}
            <div class="chakra-name">Throat Chakra</div>
          </div>
          {% when 'Third Eye Chakra' %}
          <div class="chakra-icon">
            {% render 'third-eye-chakra-icon' %}
            <div class="chakra-name">Third Eye Chakra</div>
          </div>
          {% when 'Crown Chakra' %}
          <div class="chakra-icon">
            {% render 'crown-chakra-icon' %}
            <div class="chakra-name">Crown Chakra</div>
          </div>
          {% when 'All Chakra' %}
          <div class="chakra-icon">
            {% render 'all-chakra-icon' %}
            <div class="chakra-name">All Chakra</div>
          </div>
          {% endcase %}
        </div>
        {% endfor %}
      </div>
    </div>
  </div>
</div>

<style>
  .chakra-icons {
    overflow-x: auto;
    scroll-snap-type: x mandatory;
    -webkit-overflow-scrolling: touch;
    margin-bottom: 20px;
  }

  .chakra-icon-wrapper {
    position: relative;
    display: flex;
    align-items: center;
    margin-right: 40px;
  }

  .chakra-icon-wrapper:last-child {
    margin-right: 0;
  }

  .chakra-icon-wrapper::after {
    content: "";
    position: absolute;
    top: 50%;
    right: -20px;
    transform: translateY(-50%);
    width: 1px;
    height: 80%;
    background-color: #e0e0e0;
  }

  .chakra-icon-wrapper:last-child::after {
    display: none;
  }

  .chakra-icon {
    flex: 0 0 auto;
    width: 120px;
    padding: 20px;
    text-align: center;
    scroll-snap-align: center;
    transition: transform 0.3s ease;
    cursor: pointer;
    position: relative;
    overflow: hidden;
  }

  .chakra-icon:hover {
    transform: translateY(-5px);
  }

  .chakra-icon:hover::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle at center, rgba(255, 255, 255, 0.3) 0%, rgba(255, 255, 255, 0) 70%);
    z-index: 1;
    animation: chakra-glow 2s infinite;
  }

  .chakra-icon svg {
    width: 60px;
    height: 60px;
    fill: #333333;
    margin-bottom: 10px;
    z-index: 2;
    position: relative;
    transition: transform 0.3s ease;
  }

  .chakra-icon:hover svg {
    transform: scale(1.1);
  }

  .chakra-icon:nth-child(1) svg {
    fill: #ff0000;
  }

  .chakra-icon:nth-child(2) svg {
    fill: #ff7f00;
  }

  .chakra-icon:nth-child(3) svg {
    fill: #ffff00;
  }

  .chakra-icon:nth-child(4) svg {
    fill: #00ff00;
  }

  .chakra-icon:nth-child(5) svg {
    fill: #0000ff;
  }

  .chakra-icon:nth-child(6) svg {
    fill: #4b0082;
  }

  .chakra-icon:nth-child(7) svg {
    fill: #9400d3;
  }

  .chakra-icon:nth-child(8) svg {
    fill: #ffffff;
  }

  .chakra-name {
    font-size: 14px;
    font-weight: 500;
    color: #333333;
    margin-top: 10px;
    z-index: 2;
    position: relative;
  }

  @media screen and (max-width: 768px) {
    .chakra-icon {
      width: 100px;
      padding: 10px;
    }

    .chakra-icon svg {
      width: 50px;
      height: 50px;
    }

    .chakra-name {
      font-size: 12px;
    }
  }

  @keyframes chakra-glow {
    0% {
      transform: scale(1);
      opacity: 0;
    }
    50% {
      transform: scale(1.2);
      opacity: 0.5;
    }
    100% {
      transform: scale(1.4);
      opacity: 0;
    }
  }

  .chakra-icon-animate {
    animation: spin 5s infinite linear;
  }

  @keyframes spin {
    0% {
      transform: rotate(0deg);
    }
    100% {
      transform: rotate(360deg);
    }
  }
</style>

