{% assign imageCount = 0 %}

{% for i in (1..7) %}
  {% assign imageKey = 'stoneimg' | append: i %}
  {% assign imageValue = product.metafields.custom[imageKey] %}

  {% if imageValue != blank %}
    {% assign imageCount = imageCount | plus: 1 %}
  {% endif %}
{% endfor %}

{% if imageCount > 0 %}
  <div class="product-image-slider {% if imageCount == 1 %}single-image{% endif %}">
    <div class="slider-wrapper">
      <div class="slider">
        {% for i in (1..7) %}
          {% assign imageKey = 'stoneimg' | append: i %}
          {% assign imageValue = product.metafields.custom[imageKey] %}
          {% assign linkKey = 'stoneimg' | append: i | append: 'link' %}
          {% assign linkValue = pages[product.metafields.custom[linkKey]].url %}

          {% if imageValue != blank %}
            <div class="slide">
              <a href="{{ linkValue }}">
                <img src="{{ imageValue | img_url: 'original' }}" alt="{{ product.title }} Image {{ i }}">
              </a>
              <a href="{{ linkValue }}" class="know-more-link">Know More</a>
            </div>
          {% endif %}
        {% endfor %}
      </div>
    </div>

    {% if imageCount > 1 %}
      <div class="scroll-indicator">
        <span class="scroll-dot"></span>
      </div>
    {% endif %}
  </div>

  <style>
    .product-image-slider {
      max-width: 100%;
      margin: 0 auto;
      position: relative;
      overflow: hidden;
    }

    .slider-wrapper {
      overflow-x: auto;
      -webkit-overflow-scrolling: touch;
      scroll-snap-type: x mandatory;
      scroll-behavior: smooth;
    }

    .slider {
      display: flex;
      flex-wrap: nowrap;
      scroll-snap-align: start;
    }

    .slide {
      flex: 0 0 auto;
      width: 250px;
      margin-right: 30px;
      margin-left: 10px;
      scroll-snap-align: center;
      border-radius: 10px;
      overflow: hidden;
      position: relative;
    }

    .slide img {
      width: 100%;
      height: auto;
      object-fit: cover;
      user-select: none;
      pointer-events: none;
    }

    .know-more-link {
      position: absolute;
      bottom: 10px;
      left: 50%;
      transform: translateX(-50%);
      padding: 8px 16px;
      background-color: #333;
      color: #fff;
      text-decoration: none;
      border-radius: 4px;
      font-size: 14px;
      transition: background-color 0.3s, color 0.3s;
    }

    .know-more-link:hover {
      background-color: #555;
      color: #fff; /* Text color remains white on hover */
    }

    .single-image .slider {
      justify-content: center;
    }

    .scroll-indicator {
      display: flex;
      justify-content: center;
      margin-top: 10px;
    }

    .scroll-dot {
      width: 8px;
      height: 8px;
      border-radius: 50%;
      background-color: #ddd;
      margin: 0 4px;
      transition: background-color 0.3s;
    }

    .scroll-dot.active {
      background-color: #333;
    }
  </style>

  <script>
    document.addEventListener('DOMContentLoaded', function() {
      const slider = document.querySelector('.slider');
      const sliderWrapper = document.querySelector('.slider-wrapper');
      const slides = document.querySelectorAll('.slide');
      const scrollDot = document.querySelector('.scroll-dot');
      let isDragging = false;
      let startPos = 0;
      let scrollLeft = 0;

      sliderWrapper.addEventListener('pointerdown', dragStart);
      sliderWrapper.addEventListener('pointerup', dragEnd);
      sliderWrapper.addEventListener('pointerleave', dragEnd);
      sliderWrapper.addEventListener('pointermove', drag);
      sliderWrapper.addEventListener('scroll', updateScrollIndicator);

      function dragStart(event) {
        isDragging = true;
        startPos = getPositionX(event);
        scrollLeft = sliderWrapper.scrollLeft;
      }

      function dragEnd() {
        isDragging = false;
      }

      function drag(event) {
        if (!isDragging) return;
        event.preventDefault();
        const currentPosition = getPositionX(event);
        const distance = currentPosition - startPos;
        sliderWrapper.scrollLeft = scrollLeft - distance;
      }

      function getPositionX(event) {
        return event.pageX || event.changedTouches[0].pageX;
      }

      function updateScrollIndicator() {
        const scrollPosition = sliderWrapper.scrollLeft;
        const scrollWidth = sliderWrapper.scrollWidth - sliderWrapper.clientWidth;
        const scrollPercentage = scrollPosition / scrollWidth;

        if (scrollPercentage > 0) {
          scrollDot.classList.add('active');
        } else {
          scrollDot.classList.remove('active');
        }
      }
    });
  </script>
{% endif %}
